select app_user.username,category.name,flashcard.question,flashcard.answer from app_user left join study_set on app_user.user_id = study_set.owner_id left join study_set_card on study_set_card.study_set_id = study_set.study_set_id left join flashcard on flashcard.flashcard_id = study_set_card.flashcard_id left join category on category.category_id = flashcard.category_id where study_set.owner_id = 4;
